  padding: 15px 10px;
  cursor: pointer;
}

.idyll-stepper-control-button-previous {
  position: absolute;
  left: 10px;
}

.idyll-stepper-control-button-next {
  position: absolute;
  right: 10px;
}

.idyll-stepper {
  margin: 60px 0;
}

.idyll-scroll {
  margin-top: 25vh;
}

.idyll-scroll-text {
  padding: 50vh 0;
}

.idyll-scroll-text .idyll-step {
  margin: 75vh 0 75vh 0;
  padding: 50px;
  background: white;
  border: solid 1px #333;
  box-shadow: #ddd 2px 2px 3px;
}

.idyll-root {
  padding-top: 0;
}

button {
  display: block;
  margin: 1em auto;
}

h1, h2, h3, h4, h5 {
  border-bottom: none;
}

pre {
  max-width: 960px;
  margin: 2em auto;
}

h1.hed {
  font-size: 4em;
  margin-top: 0;
}
h2.dek {
  font-size: 2em;
  margin: 0.5em auto;
  font-weight: lighter;
}
.article-header {
  background: #222;
  color: white;
  padding-top: 8em;
  padding-bottom: 4em;
  margin-bottom: 4em;
}
.article-header a {
  color: white;
  text-decoration: underline;
}
.idyll-dynamic {
  cursor: ew-resize;
  font-family: monospace;
}
.idyll-display {
  font-family: monospace;
}
img {
  display: block;
  margin: 0 auto;
}

@media all and (max-width: 1000px) {
  .idyll-root {
    max-width: none;
    padding: 0;
  }

  h1.hed {
    font-size: 2em;
  }
  h2.dek {
    font-size: 1em;
  }
}

